import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import fire from './Fire';

export default class Login extends Component {
    constructor(props){
        super(props);
        this.state={
            email:'',
            password:''

        }
    }

    login(e){
        e.preventDefault();
        fire.auth().signInAndRetrieveDataWithEmailAndPassword(this.state.email,this.state.password).then((u)=>{
        }).catch((error)=>{
            console.log(error);
        });
    }

    handleChange(e){
        this.setState({[e.target.name]: e.target.value});
    }


  render() {
    return (
        <div>
        <div className="login-page">
        <h2>&nbsp;
        &nbsp;&nbsp;&nbsp;
        &nbsp;&nbsp;&nbsp;
        &nbsp;&nbsp;&nbsp;
        &nbsp;&nbsp;&nbsp;
        &nbsp;&nbsp;LOGIN </h2>
    <div className="form">

      <form>
        <input value={this.state.email} onChange={this.handleChange} type="email" placeholder="&#xf007;  email"  />
        <input value={this.state.password} onChange={this.handleChange}  type="password" placeholder="&#xf023;  password" />

        <p className="message"></p>
      </form>
      <button onClick={this.login} type="submit">LOGIN</button>

      <form className="login-form"><Link to="/signup">
        <button id="btnSignUp" type="button">SIGN UP</button>
        </Link>
      </form>
    </div>

  </div>
     </div>
    )
  }
}
